svg.script:

var svgWrapper;  
function init(evt) {     
	svg = new $.svg._wrapperClass(         
		evt.target.ownerDocument.documentElement,
		{clientWidth: 500, clientHeight: 400});     

	var constraintGraph = [];
	var visualObjects = [];

	var svgModule = SVGHelper();
	var constraintModule = ConstraintModule(svgModule);
	var constructors = setupConstructors(null,constraintModule); 


	document.documentElement.addEventListener("mousedown",function(e){
		var o = constructors.ClassIcon(e.clientX,e.clientY);
		constraintGraph = constraintGraph.concat(o.constraints);
		visualObjects = visualObjects.concat(o.constraintVisualObjects); 

		console.log("constraintGraph",constraintGraph);
		console.log("visualObjects",visualObjects); 

		constraintModule.resolveGraphicalConstraints(visualObjects,constraintGraph);
	},false);


} //
